mapper.select:
select
from sys_fun where parentid in
#{funid,jdbcType=VARCHAR}

mapper.delete:
delete from sys_fun
 where FUNID  in

   #{funid,jdbcType=VARCHAR}

mapper.select:
select ,b.fundesc parentName,
case when exists( select 1 from sys_fun k where a.funid=k.parentid ) then 1 else 0 end hasChild
0 hasChild , case when exists( select 1 from sys_fun k where a.funid=k.parentid and k.botflag='1' ) then 1 else 0 end hasMenuChild ,c.childSize,d.childMenuSize from sys_fun a left outer join sys_fun b on a.parentid=b.funid left outer join ( select g.parentid,count(*) childSize from sys_fun g group by g.parentid ) c on a.funid=c.parentid left outer join ( select g.parentid,count(*) childMenuSize from sys_fun g where g.botflag='1' group by g.parentid ) d on a.funid=d.parentid
a.parentid='TOP'
a.parentid =#{parentid,jdbcType=VARCHAR}
LOCATE(#{funid,jdbcType=VARCHAR},a.funid)>0
and LOCATE(#{fundesc,jdbcType=VARCHAR},a.fundesc)>0
and a.botflag=#{botflag,jdbcType=VARCHAR}
and a.useflag=#{useflag,jdbcType=VARCHAR}
and a.displayfun=#{displayfun,jdbcType=VARCHAR}
order by a.ORDERVAL asc

mapper.sql:
     from sys_fun a left outer join sys_fun b
     on a.parentid=b.funid


	          and LOCATE(#{record.funid,jdbcType=VARCHAR},a.funid)>0 


	          and LOCATE(#{record.fundesc,jdbcType=VARCHAR},a.fundesc)>0 


	          and a.botflag=#{record.botflag,jdbcType=VARCHAR}


	          and a.useflag=#{record.useflag,jdbcType=VARCHAR}


	          and a.displayfun=#{record.displayfun,jdbcType=VARCHAR}

mapper.select:
select  ,b.fundesc parentName 

order by a.parentid  desc,orderval asc
limit #{start},#{rows}

mapper.insert:
insert into sys_fun_record( FUNID, ORDERVAL, LAYID, FUNDESC, FUNPATH, PARENTID, BOTFLAG, USEFLAG, DISPLAYFUN, OPERATE_USER, OPERATE_TIME, OPERATE_STATUS, SEQNO, ATTACHID ) select FUNID, ORDERVAL, LAYID, FUNDESC, FUNPATH, PARENTID, BOTFLAG, USEFLAG, DISPLAYFUN, #{userId},
#{opDate,jdbcType=TIMESTAMP} OPERATE_TIME
, #{status}, #{reqNo}, ATTACHID from sys_fun where funid=#{id}
